<!--\n  TagManager Storybook Stories\n  \n  Visual testing and documentation for TagManager component\n  Demonstrates various states and configurations\n-->\n\n<script>\n  import { Meta, Story, Template } from '@storybook/addon-svelte-csf';\n  import TagManager from './TagManager.svelte';\n  import type { Tag } from '../../utils/types.js';\n\n  // Sample tags for testing\n  const sampleTags = [\n    {\n      id: '1',\n      name: 'work',\n      color: '#2563eb',\n      createdAt: new Date('2024-01-01'),\n      conversationId: 'conv-123'\n    },\n    {\n      id: '2',\n      name: 'research',\n      color: '#16a34a',\n      createdAt: new Date('2024-01-02'),\n      conversationId: 'conv-123'\n    },\n    {\n      id: '3',\n      name: 'important',\n      color: '#dc2626',\n      createdAt: new Date('2024-01-03'),\n      conversationId: 'conv-123'\n    }\n  ];\n\n  const longTags = [\n    ...sampleTags,\n    {\n      id: '4',\n      name: 'machine-learning',\n      color: '#9333ea',\n      createdAt: new Date('2024-01-04'),\n      conversationId: 'conv-123'\n    },\n    {\n      id: '5',\n      name: 'project-alpha',\n      color: '#ca8a04',\n      createdAt: new Date('2024-01-05'),\n      conversationId: 'conv-123'\n    },\n    {\n      id: '6',\n      name: 'urgent-review',\n      color: '#c2410c',\n      createdAt: new Date('2024-01-06'),\n      conversationId: 'conv-123'\n    }\n  ];\n</script>\n\n<Meta\n  title=\"Components/TagManager\"\n  component={TagManager}\n  argTypes={{\n    conversationId: {\n      control: 'text',\n      description: 'Unique identifier for the conversation'\n    },\n    initialTags: {\n      control: 'object',\n      description: 'Initial tags to display'\n    },\n    disabled: {\n      control: 'boolean',\n      description: 'Whether the component is disabled'\n    },\n    compact: {\n      control: 'boolean',\n      description: 'Whether to use compact styling'\n    }\n  }}\n  parameters={{\n    docs: {\n      description: {\n        component: 'TagManager provides conversation tagging functionality with add/remove capabilities, color coding, and Safe Mode integration.'\n      }\n    }\n  }}\n/>\n\n<Template let:args>\n  <div style=\"max-width: 600px; padding: 1rem; background: #f9fafb; border-radius: 8px;\">\n    <h3 style=\"margin-bottom: 1rem; color: #374151;\">TagManager Component</h3>\n    <TagManager {...args} />\n  </div>\n</Template>\n\n<!-- Default State -->\n<Story\n  name=\"Default\"\n  args={{\n    conversationId: 'conv-123',\n    initialTags: [],\n    disabled: false,\n    compact: false\n  }}\n/>\n\n<!-- With Existing Tags -->\n<Story\n  name=\"With Tags\"\n  args={{\n    conversationId: 'conv-123',\n    initialTags: sampleTags,\n    disabled: false,\n    compact: false\n  }}\n/>\n\n<!-- Many Tags -->\n<Story\n  name=\"Many Tags\"\n  args={{\n    conversationId: 'conv-123',\n    initialTags: longTags,\n    disabled: false,\n    compact: false\n  }}\n/>\n\n<!-- Compact Mode -->\n<Story\n  name=\"Compact Mode\"\n  args={{\n    conversationId: 'conv-123',\n    initialTags: sampleTags,\n    disabled: false,\n    compact: true\n  }}\n/>\n\n<!-- Disabled State -->\n<Story\n  name=\"Disabled\"\n  args={{\n    conversationId: 'conv-123',\n    initialTags: sampleTags,\n    disabled: true,\n    compact: false\n  }}\n/>\n\n<!-- Empty State -->\n<Story\n  name=\"Empty State\"\n  args={{\n    conversationId: 'conv-123',\n    initialTags: [],\n    disabled: false,\n    compact: false\n  }}\n/>\n\n<!-- Single Tag -->\n<Story\n  name=\"Single Tag\"\n  args={{\n    conversationId: 'conv-123',\n    initialTags: [sampleTags[0]],\n    disabled: false,\n    compact: false\n  }}\n/>\n\n<!-- Long Tag Names -->\n<Story\n  name=\"Long Tag Names\"\n  args={{\n    conversationId: 'conv-123',\n    initialTags: [\n      {\n        id: '1',\n        name: 'very-long-tag-name-that-should-truncate',\n        color: '#2563eb',\n        createdAt: new Date(),\n        conversationId: 'conv-123'\n      },\n      {\n        id: '2',\n        name: 'another-extremely-long-tag-name',\n        color: '#16a34a',\n        createdAt: new Date(),\n        conversationId: 'conv-123'\n      }\n    ],\n    disabled: false,\n    compact: false\n  }}\n/>